targets.expr:
sum by (project_uuid, project_name) (
  oie_instance_info{
    job="openstack-instance-exporter",
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)

max by (project_uuid, project_name) (
  oie_instance_attention_severity{job="openstack-instance-exporter", instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid", project_name=~"$gvar_oie_project_name"}
) >= 60

max by (project_uuid, project_name) (
  oie_instance_attention_severity{job="openstack-instance-exporter", instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid", project_name=~"$gvar_oie_project_name"}
) >= 85

100 *
sum by (project_name, project_uuid) (
  (avg_over_time(
    oie_instance_cpu_vcpu_percent{
      instance=~"$gvar_oie_hypervisor.*",
      project_uuid=~"$gvar_oie_project_uuid",
      project_name=~"$gvar_oie_project_name"
    }[1m]
  ) / 100)
  * oie_instance_cpu_vcpu_count{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)
/
sum by (project_name, project_uuid) (
  oie_instance_cpu_vcpu_count{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)

100 *
sum by (project_name, project_uuid) (
  oie_instance_mem_used_mb{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)
/
sum by (project_name, project_uuid) (
  oie_instance_mem_allocated_mb{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)

sum by (project_uuid, project_name) (
  rate(oie_instance_disk_read_gbytes_total{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }[1m])
)

sum by (project_uuid, project_name) (
  rate(oie_instance_disk_write_gbytes_total{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }[1m])
)

sum by (project_uuid, project_name) (
  increase(oie_instance_disk_read_gbytes_total{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }[1m])
)

sum by (project_uuid, project_name) (
  increase(oie_instance_disk_write_gbytes_total{
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }[1m])
)

( sum by (project_uuid, project_name) (
  rate(oie_instance_net_rx_packets_total{instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid", project_name=~"$gvar_oie_project_name"}[1m])
))

( sum by (project_uuid, project_name) (
  rate(oie_instance_net_tx_packets_total{instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid",project_name=~"$gvar_oie_project_name"}[1m])
))

(
  sum by (project_uuid, project_name) (
    (rate(oie_instance_net_rx_gbytes_total{instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid", project_name=~"$gvar_oie_project_name"}[1m])
    * 8
    )
  )
)

(
  sum by (project_uuid, project_name) (
    (rate(oie_instance_net_tx_gbytes_total{instance=~"$gvar_oie_hypervisor.*", project_uuid=~"$gvar_oie_project_uuid", project_name=~"$gvar_oie_project_name"}[1m])
    * 8
    )
  )
)

sum by (project_uuid, project_name) (
  oie_instance_conntrack_ip_flows{
    job="openstack-instance-exporter",
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)
/
sum by (project_uuid, project_name) (
  oie_instance_info{
    job="openstack-instance-exporter",
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)

10 *
sum by (project_uuid, project_name) (
  oie_instance_conntrack_ip_flows{
    job="openstack-instance-exporter",
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)
/
scalar(
  sum(
    oie_host_conntrack_max{
      job="openstack-instance-exporter",
      instance=~"$gvar_oie_hypervisor.*"
    }
  )
)

100 *
sum by (project_uuid, project_name) (
  oie_instance_conntrack_ip_flows{
    job="openstack-instance-exporter",
    instance=~"$gvar_oie_hypervisor.*",
    project_uuid=~"$gvar_oie_project_uuid",
    project_name=~"$gvar_oie_project_name"
  }
)
/
scalar(
  sum(
    oie_host_conntrack_entries{
      job="openstack-instance-exporter",
      instance=~"$gvar_oie_hypervisor.*"
    }
  )
)

topk(20,
  sum by (instance_uuid, project_uuid, project_name, family) (
    oie_instance_conntrack_ip_flows{
      job="openstack-instance-exporter",
      instance=~"$gvar_oie_hypervisor.*",
      project_uuid=~"$gvar_oie_project_uuid",
      project_name=~"$gvar_oie_project_name"
    }
  )
)

topk(20,
  max by (instance_uuid, project_uuid, project_name, family) (
    oie_instance_inbound_unique_remotes{
      job="openstack-instance-exporter",
      instance=~"$gvar_oie_hypervisor.*",
      project_uuid=~"$gvar_oie_project_uuid",
      project_name=~"$gvar_oie_project_name"
